import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth0 } from "@auth0/auth0-react";
import { useHistory } from 'react-router-dom';

const Dashboard = () => {
  const { user, isAuthenticated } = useAuth0();
  const [wishlist, setWishlist] = useState([]);
  const history = useHistory();

  useEffect(() => {
    if (isAuthenticated) {
      // Show welcome message
      alert(`Welcome to Digital Heritage, ${user.name}!`);

      // Save user data to the database
      axios.post('/api/user/login', {
        username: user.name,
        email: user.email,
        wishlist: [] // Initialize with an empty wishlist for now
      });

      // Fetch the user's wishlist
      const fetchWishlist = async () => {
        try {
          const response = await axios.get(`/api/user/${user.email}`);
          setWishlist(response.data.wishlist || []);
        } catch (error) {
          console.error("Error fetching wishlist:", error);
        }
      };
      fetchWishlist();
    }
  }, [isAuthenticated, user]);

  const deleteData = async () => {
    try {
      await axios.delete('/api/user/delete', { data: { email: user.email } });
      alert('Your data has been deleted.');
    } catch (error) {
      console.error("Error deleting user data:", error);
    }
  };

  if (!isAuthenticated) {
    return <div>Please log in to access your dashboard.</div>;
  }

  return (
    <div className="dashboard">
      <h1>Welcome to Digital Heritage, {user.name}!</h1>
      <div className="wishlist-container">
        <h2>Your Wishlist</h2>
        {wishlist.length ? (
          <ul>
            {wishlist.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        ) : (
          <p>Your wishlist is empty.</p>
        )}
      </div>
      <button onClick={deleteData}>Delete My Data</button>
    </div>
  );
};

export default Dashboard;
