import React, { useEffect, useState } from 'react'; // Added useState for pop-up message
import './Navbar.css';
import { useAuth0 } from "@auth0/auth0-react";
import { useTranslation } from 'react-i18next';
import i18n from './i18n.js';
import { useTheme } from './ThemeContext.js';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
  const { t } = useTranslation();
  const { loginWithRedirect, isAuthenticated, logout, user } = useAuth0();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();

  const [popUpMessage, setPopUpMessage] = useState(''); // State for pop-up message

  useEffect(() => {
    if (isAuthenticated) {
      setPopUpMessage(`Welcome to Digital Heritage, ${user.name}!`);
      axios.post('/api/user/login', {
        username: user.name,
        email: user.email,
        wishlist: []
      }).catch(error => {
        console.error("Error saving user data:", error);
      });
      setTimeout(() => setPopUpMessage(''), 3000); // Remove message after 3 seconds
    }
  }, [isAuthenticated, user]);

  const handleLanguageChange = (language) => {
    i18n.changeLanguage(language);
  };

  return (
    <nav className={`navbar ${theme}`}>
      <div className="navbar-logo">
        <span className="navbar-logo-text">Digital Heritage</span>
      </div>
      <div className="navbar-buttons">
        <div className="dropdown">
          <button className="button dropdown-button">
            {t('language')}
            <svg viewBox="0 0 360 360">
              <path
                d="M325.607,79.393c-5.857-5.857-15.355-5.858-21.213,0.001l-139.39,139.393L25.607,79.393 c-5.857-5.857-15.355-5.858-21.213,0.001c-5.858,5.858-5.858,15.355,0,21.213l150.004,150c2.813,2.813,6.628,4.393,10.606,4.393 s7.794-1.581,10.606-4.394l149.996-150C331.465,94.749,331.465,85.251,325.607,79.393z"
              />
            </svg>
          </button>
          <div className="dropdown-content">
            <a href="#" className="dropdown-link" onClick={() => handleLanguageChange('en')}>English</a>
            <a href="#" className="dropdown-link" onClick={() => handleLanguageChange('hi')}>Hindi</a>
          </div>
        </div>

        <button className="button theme-toggle-button" onClick={toggleTheme}>
          {theme === 'light' ? 'ðŸŒ™' : 'â˜€'}
        </button>

        <div className="user-actions">
          {isAuthenticated ? (
            <>
              <button className="button" onClick={() => navigate('/dashboard')}>
                Dashboard
              </button>
              <button className="button login-logout-button" onClick={() => { logout({ returnTo: window.location.origin }); setPopUpMessage('You have logged out!'); }}>
                {t('logOut')}
              </button>
            </>
          ) : (
            <button className="button login-logout-button" onClick={() => loginWithRedirect()}>
              {t('logIn')}
            </button>
          )}
        </div>
      </div>
      {popUpMessage && <div className="pop-up-message">{popUpMessage}</div>} {/* Pop-up message */}
    </nav>
  );
};

export default Navbar;
