import React, { useEffect } from 'react';
import './Navbar.css';
import { useAuth0 } from "@auth0/auth0-react";
import { useTranslation } from 'react-i18next';
import i18n from './i18n.js';
import { useTheme } from './ThemeContext.js';
import { useHistory } from 'react-router-dom'; // Add history for navigation

const Navbar = () => {
  const { t } = useTranslation();
  const { loginWithRedirect, isAuthenticated, logout, user } = useAuth0();
  const { theme, toggleTheme } = useTheme();
  const history = useHistory();

  useEffect(() => {
    if (isAuthenticated) {
      // Show a welcome message after login
      alert(`Welcome to Digital Heritage, ${user.name}!`);

      // Store user data in the backend
      axios.post('/api/user/login', {
        username: user.name,
        email: user.email,
        wishlist: [] // You can adjust this based on actual wishlist data
      });
    }
  }, [isAuthenticated, user]);

  return (
    <nav className={`navbar ${theme}`}>
      <div className="navbar-logo">
        <span className="navbar-logo-text">Digital Heritage</span>
      </div>
      <div className="navbar-buttons">
        <button className="button theme-toggle-button" onClick={toggleTheme}>
          {theme === 'light' ? 'ðŸŒ™' : 'â˜€'}
        </button>

        {isAuthenticated && (
          <div className="user-info">
            <p>{t('user')}: {user.name}</p>
            <button className="button" onClick={() => history.push('/dashboard')}>
              Dashboard
            </button>
            <button className="button" onClick={() => logout({ returnTo: window.location.origin })}>
              {t('logOut')}
            </button>
          </div>
        )}
        {!isAuthenticated && (
          <button className="button" onClick={() => loginWithRedirect()}>
            {t('logIn')}
          </button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
