import React, { useState, useEffect } from 'react';
import './Quiz.css'; // Create a separate CSS file for styling

const questions = {
  heritage: [
    { question: "What is the capital of India?", options: ["Delhi", "Mumbai", "Kolkata", "Chennai"], answer: "Delhi" },
    { question: "Which monument is known as a symbol of love?", options: ["Red Fort", "Taj Mahal", "Qutub Minar", "Hawa Mahal"], answer: "Taj Mahal" },
    // Add 13 more questions related to Indian heritage
  ],
  traditions: [
    { question: "Which festival is known as the festival of lights?", options: ["Holi", "Diwali", "Eid", "Christmas"], answer: "Diwali" },
    { question: "What is the traditional attire for women in India?", options: ["Sari", "Lehenga", "Kurti", "All of the above"], answer: "All of the above" },
    // Add 13 more questions related to Indian traditions
  ],
  arts: [
    { question: "Which dance form is associated with Kerala?", options: ["Bharatanatyam", "Kathak", "Mohiniyattam", "Odissi"], answer: "Mohiniyattam" },
    { question: "What is the primary medium used in Madhubani paintings?", options: ["Watercolor", "Oil", "Ink", "Natural dyes"], answer: "Natural dyes" },
    // Add 13 more questions related to Indian arts
  ],
};

const Quiz = () => {
  const [currentSection, setCurrentSection] = useState('heritage');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [timer, setTimer] = useState(600); // 10 minutes
  const [quizCompleted, setQuizCompleted] = useState(false);

  useEffect(() => {
    if (timer > 0 && !quizCompleted) {
      const interval = setInterval(() => {
        setTimer((prevTime) => prevTime - 1);
      }, 1000);
      return () => clearInterval(interval);
    } else if (timer === 0) {
      setQuizCompleted(true);
    }
  }, [timer, quizCompleted]);

  const handleOptionClick = (option) => {
    if (option === questions[currentSection][currentQuestionIndex].answer) {
      setScore(score + 1);
    }
    if (currentQuestionIndex < 14) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setQuizCompleted(true);
    }
  };

  const handleSectionChange = (section) => {
    setCurrentSection(section);
    setCurrentQuestionIndex(0);
    setScore(0);
    setTimer(600);
    setQuizCompleted(false);
  };

  return (
    <div className="quiz-container">
      <h2>{currentSection === 'heritage' ? 'Indian Heritage Quiz' : currentSection === 'traditions' ? 'Indian Traditions Quiz' : 'Indian Arts Quiz'}</h2>
      <div className="timer">Time Remaining: {Math.floor(timer / 60)}:{('0' + (timer % 60)).slice(-2)}</div>
      
      {quizCompleted ? (
        <div className="result-popup">
          <h3>You completed your test!</h3>
          <p>Your score is: {score} out of 15</p>
        </div>
      ) : (
        <div>
          <h4>{questions[currentSection][currentQuestionIndex].question}</h4>
          <div className="options">
            {questions[currentSection][currentQuestionIndex].options.map((option) => (
              <button key={option} onClick={() => handleOptionClick(option)}>
                {option}
              </button>
            ))}
          </div>
        </div>
      )}

      <div className="sections">
        <button onClick={() => handleSectionChange('heritage')}>Heritage Quiz</button>
        <button onClick={() => handleSectionChange('traditions')}>Traditions Quiz</button>
        <button onClick={() => handleSectionChange('arts')}>Arts Quiz</button>
      </div>
    </div>
  );
};

export default Quiz;
