import React from 'react'; 
import { Route, Routes, useLocation } from 'react-router-dom'; 
import Navbar from './components/Navbar';
import Slider from './components/Slider';
import SearchBar from './components/SearchBar';
import Categories from './components/Categories';
import VrTour from './components/VrTour'; 
import Spotlights from './components/Spotlights'; 
import HeritageSites from './components/HeritageSites';
import Footer from './components/Footer';
import Dashboard from './components/Dashboard'; // Import Dashboard component
import { useTheme } from './components/ThemeContext';
import { useAuth0 } from "@auth0/auth0-react"; // Import Auth0 for authentication

function App() {
  const location = useLocation();
  const { theme } = useTheme();
  const { isAuthenticated, user } = useAuth0(); // Get authentication status and user info

  const appStyle = {
    backgroundColor: theme === 'light' ? '#ffffff' : '#333333',
    color: theme === 'light' ? '#000000' : '#ffffff',
    minHeight: '100vh',
  };

  return (
    <div className="App" style={appStyle}>
      <Navbar />
      
      {location.pathname === '/' && (
        <>
          <Slider />
          <SearchBar />
        </>
      )}

      <Routes>
        <Route path="/" element={<Categories />} />
        <Route path="/vr-tour" element={<VrTour />} />
        <Route path="/spotlights" element={<Spotlights />} />
        
        {/* Protected route for user dashboard, rendered only if authenticated */}
        {isAuthenticated && (
          <Route path="/dashboard" element={<Dashboard user={user} />} />
        )}
      </Routes>

      {location.pathname !== '/vr-tour' && location.pathname !== '/spotlights' && (
        <HeritageSites />
      )}

      <Footer />
    </div>
  );
}

export default App;
